import React, { useState } from "react";
import CreateNotes from "./CreateNotes";
import Header from "./Header";
import Note from "./Note";
import Footer from "./Footer";
const App = ()=>{
    const [addItem , setaddItem] = useState([]);

    const addNote = (note) =>{
      setaddItem((data) =>{
          return [... data , note];
      })
    }
    return(
    <>
    <Header/>,
    <CreateNotes passNote ={addNote}/>,
    <Note/>,
    <Footer/>
    </>
    )
}

export default App;
