import React, { useState } from "react";
import CreateNotes from "./CreateNotes";
import Header from "./Header";
import Note from "./Note";
import Footer from "./Footer";
const App = ()=>{
    const [addItem , setaddItem] = useState([]);

    const addNote = (note) =>{
      setaddItem((data) =>{
          return [... data , note];
      });
      console.log(note);
    }

    const onDelete = (id) =>{
        
        setaddItem((data) =>{
              return data.filter((item,index)=> {
                  return index !== id;
            });
        });
        console.log({addItem});
    };
    
    const Fav = () =>{
        const y ="yes";
        if(y === "yes"){
            <div className="allNotes">
    {
        addItem.map((val,index) => {
            return (
               <Note
                   key ={index}
                   id ={index}
                   title ={val.title}
                   content ={val.content}
                   deleteItem = {onDelete}
               />
            );
        })
    }
    </div>
        }
        
    }
    return(
    <>
    <Header/>
    <CreateNotes passNote ={addNote}/>
     {Fav()}
    <Footer/>
    </>
    );
}

export default App;
