import React, { useState } from "react";
import "./index.css";
import AddIcon from '@material-ui/icons/Add';

function CreateNotes(){
    const [note , setNote] = useState({
      title:"",
      content:"",
    });

    const InputEvent = (e)=>{
      const {name,value} = e.target;
      console.log(name , value);
    }
    
    return (

        <div className="Notes">
          <form>
              <input type="text" placeholder="Title.." onChange = {InputEvent} name = "title"/><br/>
              <textarea rows="" column="" placeholder="Take a Note.." onChange = {InputEvent}  name = "content">

              </textarea><br/>
              <button className="add">
                 <AddIcon className ="plus"/>
              </button>
          </form>
          
        </div>
    );
}
export default CreateNotes;
