import React, { useState } from "react";
import CreateNotes from "./CreateNotes";
import Header from "./Header";
import Note from "./Note";
import Footer from "./Footer";
const App = ()=>{

    const [check , ischeck] = useState(false);

    const addCheck = () =>{
        ischeck(true);
    }
    const addCheck1 = () =>{
        ischeck(false);
    }
    const [addItem , setaddItem] = useState([]);

    const addNote = (note) =>{
      setaddItem((data) =>{
          return [... data , note];
      });
      console.log(note);
    }

    const onDelete = (id) =>{
        
        setaddItem((data) =>{
              return data.filter((item,index)=> {
                  return index !== id;
            });
        });
        console.log({addItem});
    };
   
    return(
    <>
    <Header/>
    <CreateNotes passNote ={addNote}/>

    <div className="allNotes">
    {
        ischeck ?
        addItem.map((val,index) => {
            return (
               <Note
                   key ={index}
                   id ={index}
                   title ={val.title}
                   content ={val.content}
                   deleteItem = {onDelete}
               />
            );
        })
        :<Note/>
    }
    </div>
    <button onClick = {addCheck}></button>
    <button onClick = {addCheck1}></button>
    <Footer/>
    </>
    );
}

export default App;
