import React, { useState } from "react";
import "./index.css";
import AddIcon from '@material-ui/icons/Add';

function CreateNotes(){
    const [inputValue , setInput] = useState("");
    const [textValue , setText] = useState("");

    const [myarr , setArr] = useState([{
        title:"",
        text:""
    }]);
    

    const InputEvent = (event) =>{
      setInput(event.target.value);
    }
    const TextEvent = (event) =>{
        setText(event.target.value);
      }

    const finalData = (event) =>{
        event.preventDefault();
        setArr({inputValue,textValue});
        setInput("");
        setText("");
    }
    
    return (

        <div className="Notes">
          <form>
              <input type="text" placeholder="Title.." onChange = {InputEvent} value ={inputValue} name = "title"/><br/>
              <textarea rows="" column="" placeholder="Take a Note.." onChange = {TextEvent} value ={textValue} name = "content">

              </textarea><br/>
              <button className="add" onClick = {finalData}>
                 <AddIcon className ="plus"/>
              </button>
          </form>
          
        </div>
    );
}
export default CreateNotes;
